import React, { useState, useEffect } from 'react';
import ChatInterface from './components/ChatInterface';
import ApiKeyModal from './components/ApiKeyModal';
import './App.css';

function App() {
  const [apiKey, setApiKey] = useState(localStorage.getItem('openai_api_key') || '');
  const [showApiModal, setShowApiModal] = useState(!apiKey);

  const handleApiKeySubmit = (key) => {
    setApiKey(key);
    localStorage.setItem('openai_api_key', key);
    setShowApiModal(false);
  };

  const handleApiKeyChange = () => {
    setShowApiModal(true);
  };

  useEffect(() => {
    // Check if API key exists on app load
    if (!apiKey) {
      setShowApiModal(true);
    }
  }, [apiKey]);

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1>ðŸ§® Math Learning Chat</h1>
          <p>Upload images, get questions, and learn math interactively</p>
          <button 
            className="api-key-button"
            onClick={handleApiKeyChange}
          >
            {apiKey ? 'ðŸ”‘ Change API Key' : 'ðŸ”‘ Set API Key'}
          </button>
        </div>
      </header>

      <main className="App-main">
        {apiKey ? (
          <ChatInterface apiKey={apiKey} />
        ) : (
          <div className="no-api-key">
            <p>Please set your OpenAI API key to start using the chat.</p>
            <button 
              className="set-api-key-button"
              onClick={() => setShowApiModal(true)}
            >
              Set API Key
            </button>
          </div>
        )}
      </main>

      {showApiModal && (
        <ApiKeyModal 
          onSubmit={handleApiKeySubmit}
          onClose={() => setShowApiModal(false)}
        />
      )}
    </div>
  );
}

export default App;
